function HTMLFormatter(){
    //empty constructor
}

HTMLFormatter.startDocument = function (){
    return "<html></br>Project files:";
}

HTMLFormatter.endDocument = function (){
    return "</html>";
}

HTMLFormatter.startList = function (){
    return "<ul>";
}

HTMLFormatter.endList = function (){
    return "</ul>";
}

HTMLFormatter.printFileInList = function (currentDirFiles,index){
    return "<li>" + currentDirFiles.nameList[index] + " (" + currentDirFiles.mimeTypeList[index] + ")";
}

HTMLFormatter.printDirectoryInList = function (currentDirFiles,index){
    return "<li><a href='?path="+currentDirFiles.currentRelativePath + currentDirFiles.nameList[index]+"'>" + currentDirFiles.nameList[index] + "</a>";
}

HTMLFormatter.readDirError = function(){
    return "<h3>Reading directory error !</h3>";
}


exports.HTMLFormatter = HTMLFormatter;